function uploaderGetPreview(input){var extension=input.value.substring(input.value.lastIndexOf('.')+1).toLowerCase();if(extension=="gif"||extension=="png"||extension=="bmp"||extension=="jpeg"||extension=="jpg"){if(input.files&&input.files[0]){var MAX_SIZE=validateMaxImageSize;var size=input.files[0].size;if(size>MAX_SIZE){alert("Oops! Image size is too large. Max image size is "+MAX_SIZE/1000000+"MB. Please try again.");return}else{var reader=new FileReader();reader.onload=function(e){$('#'+input.id+"Preview").css('background-image','url('+e.target.result+')');$('#'+input.id+"Preview").css('top',0);$('#remove-icon-'+input.id).css('display','block')}
reader.readAsDataURL(input.files[0])}}}else{alert("Oops! only file types of GIF, PNG, JPG, JPEG and BMP are allowed. ")}}
function uploaderRemovePreview(div){var inputId=$(div).attr('data-input-id');document.getElementById(inputId).value="";$("#"+inputId+"Preview").css('background-image','url()');$("#"+inputId+"Preview").css('top','100%');$(div).css('display','none')}