<?php// watermark.php// Path the the requested file$path = $_SERVER['DOCUMENT_ROOT'].$_SERVER['REQUEST_URI'];// Load the requested image$image = imagecreatefromstring(file_get_contents($path));$w = imagesx($image);$h = imagesy($image);// Load the watermark image$watermark = imagecreatefrompng('watermark.png');$ww = imagesx($watermark);$wh = imagesy($watermark);// Merge watermark upon the original imageimagecopy($image, $watermark, $w-$ww, $h-$wh, 0, 0, $ww, $wh);// Send the imageheader('Content-type: image/jpeg');imagejpeg($image);exit();?>